void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;


    vec2 circlePos = vec2(cos(iTime), sin(iTime)) * 0.1 + 0.5;

    float dist = distance(uv, circlePos);
    float circle = step(dist, 0.3);

    vec3 background = vec3(1.0 - circle);
    vec3 circleColor = vec3(1.0 - smoothstep(0.0, 0.5, dist));

    fragColor = vec4((background + circleColor) * 1.0,1.0);
}
