void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 skyCol = vec3(0.568, 0.831, 0.941);
    vec3 sunCol = vec3(.847, .847, 0.);
    float outlineWidth = .015;

    vec3 hill1Col = vec3(0., .25, .07);
    vec3 hill2Col = vec3(0., .529, .141);
    vec3 hill3Col = vec3(0., .847, .227);

    float hill1Amplitude = 0.1;
    float hill2Amplitude = 0.05;
    float hill3Amplitude = 0.01;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float wave1 = 0.3 + sin(uv.x * 6.0 + iTime * 0.25) * hill1Amplitude;
    float wave2 = 0.2 + sin(uv.x * 6.0 + iTime * 0.5 + 10.) * hill2Amplitude;
    float wave3 = 0.1 + sin(uv.x * 6.0 + iTime + 20.) * hill3Amplitude;

    vec2 sphereUV = uv - vec2(.5, .2);  // Position in sky (0.5 horizontal center, 0.7 vertical position)
    sphereUV.x *= iResolution.x/iResolution.y;  // Correct for aspect ratio
    float sphere = length(sphereUV) - 0.3;
    float sphereMask = 1.0 - smoothstep(0., .003, sphere);  // Smooth the edges


    vec3 finalColor = skyCol;
    finalColor = mix(finalColor, sunCol, sphereMask);

    finalColor = mix(vec3(0.), finalColor, step(wave1 + outlineWidth, uv.y)); // Outline
    finalColor = mix(hill1Col, finalColor, step(wave1, uv.y));

    finalColor = mix(vec3(0.), finalColor, step(wave2 + outlineWidth, uv.y)); // Outline
    finalColor = mix(hill2Col, finalColor, step(wave2, uv.y));

    finalColor = mix(vec3(0.), finalColor, step(wave3 + outlineWidth, uv.y)); // Outline
    finalColor = mix(hill3Col, finalColor, step(wave3, uv.y));

    fragColor = vec4(finalColor, 1.0);
}
