void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // create pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;

    // create music map
	vec4 musicMap;
    musicMap.r = sin(uv.x);    
    musicMap.b = sin(uv.y);

    // use music map red data
    float musicMapOut = texture( iChannel0, vec2( musicMap.r,0.0)).r;

    //use music map blue data
	if(musicMapOut>musicMap.b){
		fragColor = vec4(0.0,0.0,1.0,0.0);
    }else{
		fragColor = vec4(1.0,0.0,1.0,1.0);   
    }
}
