float g_cell_size;
const float ONE_SIXTH = .1666666;

// assumes all inputs to hsl are normalized to be from 0 to 1
vec3 hsl_to_rgb(vec3 hsl)
{
    float c = (1. - abs(2. * hsl.z - 1.0)) * hsl.y;
    float x = c * (1. - abs(mod(hsl.x * 6., 2.) - 1.));
    float m = hsl.z - c/2.;

    vec3 cp;
    if (hsl.x < ONE_SIXTH)
    {
        cp = vec3(c, x, 0.);
    }
    else if (hsl.x < 2. * ONE_SIXTH)
    {
        cp = vec3(x, c, 0.);
    }
    else if (hsl.x < 3. * ONE_SIXTH)
    {
        cp = vec3(0., c, x);       
    }
    else if (hsl.x < 4. * ONE_SIXTH)
    {
        cp = vec3(0., x, c);
    }
    else if (hsl.x < 5. * ONE_SIXTH)
    {
        cp = vec3(x, 0., c);
    }
    else
    {
        cp = vec3(c, 0., x);
    }

    vec3 rgb = vec3(cp.x + m, cp.y + m, cp.z + m);

    return rgb ;
}

vec3 convert_to_debug_color(float x)
{        
    const float num_steps = 40.;
    // we want 1 to be super hot (red) and 0. to be super cool (purple)
    float remapx = clamp(1. - x, 0., 1.);

    // avoid looping back on the hue wheel since we want to differentiate red as 1
    // purple as 0.
    float stepx = .8 * floor(num_steps * remapx)/num_steps;    
    float foot = fract(x * 10.);

    float fmask = 1.;

    vec3 hsl = vec3(stepx, .95, .5 - .1 * mod(stepx * num_steps, 2.) );
    return fmask * hsl_to_rgb(hsl);       
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    g_cell_size = 10.;//iResolution.y * .02;

    vec2 lookup = vec2(fragCoord/g_cell_size);
    vec2 uv = fragCoord/iResolution.xy;

    vec3 p = texelFetch(iChannel0, ivec2(lookup), 0).rgb;
    float x = .07 * p.r;

	vec3 a = vec3(.1, .1, 1.);
    vec3 b = vec3(.5, .5, 1.);
    vec3 c = vec3(.5, .5, .5);
    vec3 d = vec3(-.2, .5, .5);

    vec2 cellCenter = (.5 - .5 * p.gb) * g_cell_size + floor(lookup) * g_cell_size;

    float l = smoothstep(g_cell_size, 
                         0., 
                         length(fragCoord - cellCenter));
    l = .7 + .3 * l;

    vec3 scol = (a + b * sin(c * x + d)) * l;                  
    scol *= smoothstep(p.r, 0., 2.);
	//scol = convert_to_debug_color(x) * l;

    vec2 vig = uv * (1. - uv.xy);    
    scol *= pow(vig.x * vig.y * 10., .5);    

    fragColor.rgb = 1.4 * pow(scol, vec3(.45));
    fragColor.a = 1.0f;

}

float g_cell_size;
float g_neuron_threshold;

const int FRAMES_BETWEEN_BURSTS = 60;

vec2 cell_noise( in vec3 p )
{
    float offset = (iChannelResolution[1].x * iChannelResolution[1].y) * texelFetch(iChannel2, ivec2(p.xy), 0).x; 
    vec2 x = vec2(mod(offset, iChannelResolution[1].x),
                  offset/iChannelResolution[1].y);
    vec2 lookup = vec2(mod(x.x + p.z, iChannelResolution[1].x), 
                       x.y + (x.x + p.z)/iChannelResolution[1].x);
    return textureLod( iChannel1, (lookup+ 0.5)/iChannelResolution[1].x, 0.0 ).xy;
}

vec2 cell_center(vec2 fragCoord)
{    
    vec2 floored_uv = floor(fragCoord/g_cell_size);
    ivec2 noise_lookup = ivec2(floored_uv);    
    vec2 floored_coord = g_cell_size * floored_uv;    
    return floored_coord + g_cell_size * .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    

    g_cell_size = 10.;
    vec2 uv = (fragCoord/iResolution.xy);

    //g_neuron_threshold = .5 + .3 * sin(.25 * iChannelTime[3] + 3.14);
    g_neuron_threshold = .9;
    vec2 fragBounds = vec2(iResolution/g_cell_size);

    vec3 scol = vec3(0.);        

    vec4 accum = vec4(0.);
    vec3 this_color = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;
    float this_value = this_color.r;
    vec2 this_flow = this_color.gb;

    float this_alpha = 1.;

    if (iFrame%2 == 0)
    {
        if (fragCoord.x < 0. || fragCoord.x >= fragBounds.x || 
            fragCoord.y < 0. || fragCoord.y >= fragBounds.y)
        {
            this_color = vec3(0., 0., 0.);
        }     
        else
        {
            vec2 this_center = cell_center(g_cell_size * fragCoord);

            for (int i = -1; i <= 1; i += 1)
            {
                for (int j = -1; j <= 1; j += 1)
                {
                    if (i == 0 && j == 0) {
                        continue;
                    }

                    vec2 neighbor_offset = vec2(i, j);
                    vec2 neighbor_coord = fragCoord + neighbor_offset;
                    vec3 neighbor_color = texelFetch(iChannel0, ivec2(neighbor_coord), 0).rgb;
                    vec2 neighbor_center = cell_center(g_cell_size * (fragCoord + neighbor_offset));

                    if (neighbor_coord.x < 0. || neighbor_coord.x >= fragBounds.x || 
                        neighbor_coord.y < 0. || neighbor_coord.y >= fragBounds.y)
                    {
                        neighbor_color = vec3(0.);
                    }     

                    float neighbor_value = neighbor_color.r;
                    vec2 neighbor_flow = neighbor_color.gb;

                    float diff = neighbor_value - this_value;
                    vec2 neighbor_vec = this_center - neighbor_center;
                    float l = max(0.05, dot(normalize(neighbor_vec), neighbor_flow));
                    diff *= l;
                    diff = clamp(diff, 0., 1.);

                    if (diff > g_neuron_threshold) {
                        accum.r += neighbor_value * diff;
                        accum.a += diff;
                    } else if (diff < -g_neuron_threshold) {
                        this_alpha -= diff;
                    }
                }
            }
        }
    }


    this_alpha = max(0.0, this_alpha);
    // the system slowly loses energy
    accum.r += .99 * this_value * this_alpha;
    accum.a += this_alpha;

    accum.rgb /= accum.a;

    scol.r = max(0., accum.r);
    scol.gb = 2. * cell_noise(vec3(fragCoord.xy, 1. * iTime)) - 1.;

    int v = iFrame/FRAMES_BETWEEN_BURSTS + int(iDate.z);
    ivec2 seed = ivec2(v%int(iChannelResolution[1]), v/int(iChannelResolution[1])); 
    vec3 impulse_sample = texelFetch(iChannel1, seed, 0).rgb;

    if (iFrame%FRAMES_BETWEEN_BURSTS == 0)
    {        

        ivec2 impulse_loc = ivec2(impulse_sample.xy * fragBounds);
        if (impulse_loc == ivec2(fragCoord))
        {
            scol += vec3(50.) * (impulse_sample.z + .5) ;
        }
    }	

    if (ivec2(iMouse.zw / g_cell_size) == ivec2(fragCoord)) 
    {

        scol += vec3(4.0);
	}

    fragColor = vec4(scol,1.0);
}
