void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    // buffer A is drawn into using the blur shader (which 
    // reads from buffer A, drawn by feedback shader, which reads from buffer B...)

    vec4 bufferA = texture(iChannel0, uv);
    fragColor = bufferA;
}

// box blur

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 src = fragCoord / iResolution.xy;

    vec2 offs = vec2(1. / iResolution.x, 1. / iResolution.y);

    // distance of neighborhood texels (effectively half of the blur kernel width)
    float width = 0.5;
    vec2 tc4 = src;
    vec2 tc1 = src + vec2(0.0, -offs.t * width);
    vec2 tc3 = src + vec2(-offs.s * width, 0.0);
    vec2 tc5 = src + vec2(offs.s * width, 0.0);
    vec2 tc7 = src + vec2(0.0, offs.t * width);

    vec2 tc0 = src + vec2(-offs.s * width, -offs.t * width);
    vec2 tc2 = src + vec2(offs.s * width, -offs.t * width);
    vec2 tc6 = src + vec2(-offs.s * width, offs.t * width);
    vec2 tc8 = src + vec2(offs.s * width, offs.t * width);

    // texels come from iChannel0 (assigned to buffer B)
    vec4 col0 = texture(iChannel0, tc0);
    vec4 col1 = texture(iChannel0, tc1);
    vec4 col2 = texture(iChannel0, tc2);
    vec4 col3 = texture(iChannel0, tc3);
    vec4 col4 = texture(iChannel0, tc4);
    vec4 col5 = texture(iChannel0, tc5);
    vec4 col6 = texture(iChannel0, tc6);
    vec4 col7 = texture(iChannel0, tc7);
    vec4 col8 = texture(iChannel0, tc8);

    fragColor = (col4 + col2 + col0 + col6 + col8) * 0.19;
}

precision highp float;

vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 tc = fragCoord / iResolution.xy;
    vec4 imageIn = texture(iChannel1, tc);

    vec4 feedbackIn = texture(iChannel0, tc);

    vec3 s = rgb2hsv(feedbackIn.rgb);

    // length of the saturation and value components as a 2d vector
    float d = length(s.bg);

    // length of the hue and value components as a 2d vector
    float e = length(s.rg);

    // this texture fetch will be smeared according to the color of the input
    float prelookAmount = 2.1;
    vec2 prelookDisplace = vec2(-d * prelookAmount, -e * prelookAmount);
    vec4 prelook = texture(iChannel0, tc + prelookDisplace);
    /*
    d += (prelook.b * 2.0);
    d -= length(prelook) * 2.0;
    e += distance(s.gr, rgb2hsv(prelook.rgb).rg) * 2.0;
    */
    // texture fetch that will be used as the result
    // it is displaced by input from the first (prelook) texture read
    float displaceAmount = -1.0 / 420.0;
    vec2 displace = vec2(d * displaceAmount, e * displaceAmount);

    vec4 result = texture(iChannel0, tc + displace);

    // one white gain, and one blue
    // this compensates for loss in the rest of the feedback loop
    result += 0.087;
    result += 0.187 * vec4(0.1, 0.1, sin(iTime), 1.0);

    // the prelook value is subtracted from the result for a crude, 
    // spatial filtering effect similar to a sharpen filter
    result -= (prelook * 0.16);

    // thanks mikebecause
    if(iFrame <= 10 || iMouse.z > 0.0){
        fragColor = imageIn;
    } else {
        fragColor = result;
    }
}
