vec2 triChecker(vec2 p, float s)
{
    p.x += p.y * .5 ;
    vec2 m = mod(p,s); 
    vec2 base = p - m;
    p = m / s; 

    base.x *= 2. + step(p.x,p.y);

    return base;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec2 tp = triChecker(uv,.1);

	fragColor = texture(iChannel0,tp);
}
