// Couldn't resist to bring this at least to under 512b :P
// See the 128b original at https://www.pouet.net/prod.php?which=65604
// Or directly as video at https://www.youtube.com/watch?v=HIFY5AETrlM
// Feel free to use any of this. If you got ideas on how to make
// the XOR pattern faster or smaller, i'd be curious to hear them =)
void mainImage(out vec4 f,in vec2 k )
{
	vec2 uv = k.xy/iResolution.xy - .5;
    uv.x *= 1.5;
    float a = atan(uv.y,uv.x)/3.14 + iTime/20. + 9.,r = length(uv),
    c1=float(r > .1),c2 = float(r < .49),c3 = float(r > .51),v = 0.,p = 2.;
    r = .2 / r + iTime / 4.;
    for(int i = 9;i > 0;i--)
    {
        p /= 2.;
		int m = 0;
        if (a > p)m++;
        if (r > p)m++;
        a = mod(a,p);
        r = mod(r,p);
        if (m == 1)v += p;
    }
    f = vec4(mod(v*8.,1.),mod(v*4.,1.),mod(v,2.),0)*c1*c2+c3;
}
