void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy*(iTime+20.)*10.;

        uv.y =atan (uv.x,uv.y)+sin(uv.x);




    vec4 col =  vec4(0,0,cos(uv.x),1.0);
            uv.x=sin(iTime);

    vec4 mixed = mix(col,vec4(1,1,1,1),sin(uv.x));
            uv.x-=uv.y;


        vec4 mixed2 = mix(col,vec4(1,1,1,1),sin(uv.x));
		mixed = mixed2-mixed;

   fragColor = mixed;
}
