void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
	vec2 uv = fragCoord.xy / iResolution.xy;

    float value = sin(fragCoord.x / 100.0 + iTime * 5.0) * (iResolution.y / 2.0) + iResolution.y / 2.0 - fragCoord.y;
    float value2 = sin(fragCoord.x / 100.0 + iTime * 3.0) * (iResolution.y / 2.0) + iResolution.y / 2.0 - fragCoord.y;
    float value3 = cos(fragCoord.x / 100.0 + iTime * 2.0) * (iResolution.y / 2.0) + iResolution.y / 2.0 - fragCoord.y;
    float thickness = 2.0;
    float full_1 = abs(value - value2);
    if (value2 < thickness && value > -thickness)
        fragColor = vec4(value / full_1, 0.0, 0.0, 1.0);    

    if (value < thickness && value2 > -thickness)
        fragColor += vec4(0.0, value2 / full_1, 0.0, 1.0);

    float full_2 = abs(value3 - value2);
    if (value3 < thickness && value2 > -thickness)
        fragColor += vec4(0.0, 0.0, value2 / full_2, 1.0);   
    // v2 below    

    if (value2 > thickness && value3 > -thickness)
        fragColor += vec4(full_2 / value2 * 0.4, 0.0, 0.0, 1.0) / 2.0;     

    if (value > thickness && value2 > -thickness)
        fragColor += vec4(0.0, 0.0 , full_1 / value2 * 0.4, 1.0);   

    if (value2 < thickness && value < -thickness)
        fragColor = vec4(value2 / value * 0.3,  (value3 / value2)* 0.5, value / value2 * 0.3, 1.0);  
}
