float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float distanceTo(float x, float y, float centerX, float centerY) {
	float deltaX = x - centerX;
    float deltaY = y - centerY;
    return sqrt(deltaX * deltaX + deltaY * deltaY);
}

void mainImage( out vec4 color, in vec2 pixCoords )
{
    float zoom = (iResolution.x / 5.0);// * (1.0 + 0.2 * cos(2.0 * iTime));
    float cameraX = 2.5;  // + 4.0 * cos(iTime);
    float cameraY = 1.5; // + 4.0 * sin(iTime * 1.1);

    float x = (pixCoords.x / zoom) - cameraX;
    float y = (pixCoords.y / zoom) - cameraY;

    float lineSemiWidth = 0.05;

    if (abs(x) < lineSemiWidth || abs(y)<lineSemiWidth) {
		color = vec4(1.0, 0.0, 0.0, 1.0);
        return;
    }


    if (mod(x, 1.0) < lineSemiWidth || mod(y, 1.0) < lineSemiWidth) {
		color = vec4(0.5, 0.5, 0.5, 1.0);
        return;
    }

    float angle = atan(y, x);
    float radius = 1.0 + 0.5 * cos(20.0 * angle + 3.0 * iTime);

    if (distanceTo(x, y, 0.0, 0.0) < radius) {
        color = vec4(0.5, 0.5, 0.5, 1.0);
        return;
    }

    color = vec4(1.0);
}
