void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(0.0,0.0,0.0,1.0);

    float zoom = 70.0*cos(iTime*(iResolution.x/10.0));
    float newCoordsX = 5.0;
    float newCoordsY = 1.5;

    float centerX = (fragCoord.x / zoom) - newCoordsX;
    float centerY = (fragCoord.y / zoom)/1.5 - newCoordsY;

    float x = (fragCoord.x / zoom) - newCoordsX;

    float radius = sqrt((centerX*centerX) + (centerY*centerY));

    if (radius < 1.0)
    {
        fragColor = vec4(1.0,1.0,1.0,1.0);
    }

    float PI = 3.1415;
    float hauteurMoyenne = -0.3;
    float amplitude = 0.1;
    float phase = 0.5 + 10.0 * (iTime);
    float periode = 2.0;
    float fx = hauteurMoyenne + cos(x * 4.0 * PI / periode + phase) * amplitude;

    if (centerY < fx) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        return;
    }




    float oeilDroitX = (fragCoord.x / zoom) - (newCoordsX + 0.4);
    float oeilDroitY = (fragCoord.y / zoom) - (newCoordsY + 1.5);
    float radius2 = sqrt((oeilDroitX*oeilDroitX) + (oeilDroitY*oeilDroitY));

        if (radius2 < 0.2 && oeilDroitX>=oeilDroitY)
    {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }

    float oeilGaucheX = (fragCoord.x / zoom) - (newCoordsX - 0.4);
    float oeilGaucheY = (fragCoord.y / zoom) - (newCoordsY + 1.5);
    float radius3 = sqrt((oeilGaucheX*oeilGaucheX) + (oeilGaucheY*oeilGaucheY));

        if (radius3 < 0.2 && -oeilGaucheY>=oeilGaucheX)
    {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }

}
