void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float f3 = 1.0 - abs(uv.y + sin(uv.x * 3.14159 * 4.0 + iTime)*0.5 - 0.5);
    float f2 = 1.0 - abs(uv.y + sin(uv.x * 3.14159 * 3.0 + iTime)*0.5 - 0.5);
    float f = 1.0 - abs(uv.y + sin(uv.x * 3.14159 * 2.0 + iTime)*0.5 - 0.5);
    vec4 color = vec4(pow(f,3.0)*pow(f2,30.0), f*f*f*f2, pow(f3,4.0) * f2 * f,1);

    fragColor = color;
}
