float hexpain(float d, vec2 n, vec2 coord)
{
    const float sides =  12.f;
    float ang = (atan(n.y, n.x) / 3.1415 + 1.0) * 0.5;
    float qang = floor(ang * sides) / sides;
    const float seg = (3.1415 * 2.f) / (sides * 2.f);
    float v = dot(coord, vec2(sin(3.1415 * (qang) + seg), cos(3.1415 * (qang) + seg)));
    return (sin((v + iTime  * 0.1f) * 200.f));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ((fragCoord/iResolution.xy));

    // Distance field lookups
    float dr = texture(iChannel0, uv).r;
    vec2 nr = normalize(vec2(dFdx(dr), dFdy(dr)));
    float dg = texture(iChannel0, uv).g;
    vec2 ng = normalize(vec2(dFdx(dg), dFdy(dg)));
    float db = texture(iChannel0, uv).b;
    vec2 nb = normalize(vec2(dFdx(db), dFdy(db)));

    // Process eveything - inefficient hack!
    vec3 col = vec3(hexpain(dr, nr, uv), hexpain(dg, ng, uv), hexpain(db, nb, uv));

   	fragColor.rgb = col;
}
