// Fern IFS golfing inspired from iq's https://www.shadertoy.com/view/lst3zf
// proba: 1% stem, 84% next row, 8% mirror side, 8% left side

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = 12. * u/R - vec2(5.5,1),
         z = U;
    O = texture(iChannel0, u/R);
    for( float p, i=0.; i<3e2; i++ )
        p = fract( 1e4* sin( U.x*73.+U.y*7. + iTime + i ) ),
        z = vec3(z,1)* (/* z = zM+T. Mx  Tx      My    Ty    */
              p < .01 ? mat2x3(  0,0,0,0 , .16, 0   )   // mat2x3(  O-O, .16, 0   ) no longer compiles
            : p < .84 ? mat2x3( .85, .04, 0, -.04, .85, 1.6 )
            : p < .92 ? mat2x3( .20,-.26, 0,  .23, .22, 1.6 )
            :           mat2x3(-.15, .28, 0,  .26, .24,  .4 ) ),
        i>32. ? O += .002 / dot(U-z,U-z) : O; }
