vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 rgb = texture(iChannel0, uv).xyz;

    vec3 hsv = rgb2hsv(rgb);

    //float saturationFactor = texture(iChannel1, uv).x;
    //float valueFactor = saturationFactor;
    //float mixFactor = 1.0 - saturationFactor;
    float saturationFactor = uv.y;
    float valueFactor = uv.y;
    float mixFactor = uv.y+ 0.4;

    hsv.y *= saturationFactor;
    hsv.z *= valueFactor;

    vec3 rgbmod = hsv2rgb(hsv);

    vec3 mixcolor = vec3(.5, 0.5, 0.5);

	fragColor = vec4(mix(mixcolor, rgbmod, mixFactor), 1.0);
}
