const float RADIUS = 0.02;
const vec3 GLOW_COLOR = vec3(0.2, 0.5, 0.0);

float easeInOutCubic(float f) {
    const float d = 1.0;
    const float b = 0.0;
    const float c = 1.0;
    float t = f;
    if ((t /= d / 2.0) < 1.0) return c / 2.0 * t * t * t + b;
    return c / 2.0 * ((t -= 2.0) * t * t + 2.0) + b;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 glowPoint = vec2(sin(iTime), cos(iTime));
    glowPoint /= 2.5;
    glowPoint += 0.5;

    vec2 aspect = vec2(1.0, iResolution.y / iResolution.x);
	vec2 uv = fragCoord.xy / iResolution.xy;

    float glowIntensity = 0.0;

    float dist = distance(uv * aspect, glowPoint * aspect);
    vec3 glowColor = GLOW_COLOR;
    if (dist <= RADIUS) {
        glowIntensity = 1.0 - (dist / RADIUS);
        glowColor = pow(glowColor, vec3(1.0 - glowIntensity));
        glowIntensity = easeInOutCubic(glowIntensity);
        glowIntensity *= 0.9;
    }

    vec4 color = texture(iChannel0, uv);
	fragColor = color;

    if (glowIntensity > 0.0) {
        fragColor.rgb = mix(fragColor.rgb, glowColor, glowIntensity);
    }
}
