vec2 brush( vec2 p, float s, vec2 i, vec2 s2 )
{
    vec2 x = fract(p) - s * length(i) - 1.0;
    return smoothstep(s - s2.x, s2.y, sqrt(pow(length(x), max(i.x, i.y) - s)) + p);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime / 60.0 / length(fragColor) * clamp(sin(iTime/60.0), fragColor.x, -fragCoord.y);
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, length(uv.y) * brush(uv, 2.0, normalize(uv), uv)+length(uv) + sqrt(length(uv))+time);
    fragColor += vec4(uv, dot(brush(uv, 2.0, normalize(uv), uv)+length(uv)+sqrt(length(uv))+acos(time/iResolution.x), -uv), 1.0);
}
