// maze from https://www.shadertoy.com/view/4scGWf

#define T(u,v) texture( iChannel0, (U+vec2(u,v))/iResolution.xy )

void mainImage( out vec4 O,  vec2 U )
{  
    O = iFrame<150 || iMouse.z>0.
        ? .1/ fract( sin(1e5*length (ceil(U/=8.))) < 0.  ? U.x : U.y ) +O-O // maze
        : T(0,0);

    vec4 M = T(-1,0)+T(1,0)+T(0,-1)+T(0,1);                         // neighborhood
    iMouse.xy==U-U && U==vec2(2.5)  ||                              // auto-demo
    iMouse.xy==U-.5 ||  O.g<.5  &&  M.x-M.y > .5 ? O.x++ : 0.;      // pick or reached by flood
}
