#define S(a,b,t) smoothstep(a,b,t)
#define sat(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t){
	return sat((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t){
    return sat((t-a)/(b-a)) * (d-c) + c;
}

vec2 within(vec2 uv, vec4 rect){
	return (uv-rect.xy) / (rect.zw-rect.xy); //create box to draw within 
}

vec4 Eye(vec2 uv){
    uv -= .5;

    float d = length(uv);

    vec4 irisCol = vec4(.3, .5, 1., 1.); //iris color
    vec4 col = mix(vec4(1.), irisCol, S(.1, .7, d)*.5); //eye color

    col.rgb *= 1.- S(.45, .5, d)*.5 * sat(-uv.y-uv.x); //shadow 

    col.rgb = mix(col.rgb, vec3(0.), S(.3, .28, d)); //black outline of iris

    irisCol.rgb *= 1. + S(.3, .05, d); //gradient inside iris
    col.rgb = mix(col.rgb, irisCol.rgb, S(.28, .25, d)); //blue inside iris

    col.rgb = mix(col.rgb, vec3(0.), S(.15, .14, d)); //black inside blue

    //highlights
    float highlight = S(.1, .09, length(uv- vec2(-.15, .15))); //top highlight
    highlight += S(.07, .05, length(uv+ vec2(-.08, .08))); //bottom highlight
    col.rgb = mix(col.rgb, vec3(1.), highlight);


    col.a = S(.5, .48, d); //draw eye

    return col;
}

vec4 Mouth(vec2 uv){
    uv -= .5;
    vec4 col = vec4(.5, .18, .05, 1.);
    uv.y *= 1.5;
    uv.y -= uv.x*uv.x *2.;

    float d = length(uv);
    col.a = S(.5, .48, d);

    //teeth
    float td = length(uv-vec2(0., .6));
    vec3 toothCol = vec3(1.) * S(.6, .35, d);
    col.rgb = mix(col.rgb, toothCol, S(.4, .37, td));

    //tongue
    td = length(uv+vec2(0., .5));
    col.rgb = mix(col.rgb, vec3(1., .5, .5), S(.5, .2, td));

    return col;
}

vec4 Head(vec2 uv){
    vec4 col = vec4(.9, .65, .1, 1.);

    float d = length(uv);

    col.a = S(.5, .49, d);

    float edgeShade = remap01(.35, .5, d);
    edgeShade += edgeShade;

    col.rgb *= 1. - edgeShade *.5;
    col.rgb = mix(col.rgb, vec3(.6, .3, .1), S(.47, .48, d)); //creating border

    float highlight = S(.41, .405, d)*.5;
    highlight *= remap(.41, .0, .75, 0., uv.y); //gradient of face
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    //cheek
    d = length(uv-vec2(.25, -.2)); //location of cheek
    float cheek = S(.2, .1, d)*.4; //color of cheek
    cheek *= S(.18, .16, d); //edge of cheek
    col.rgb = mix(col.rgb, vec3(1., .1, .1), cheek);

    return col;
}

vec4 Smiley(vec2 uv){
    vec4 col = vec4(0.);

    uv.x = abs(uv.x); //mirros screen, everything on one side will be drawn on the other

    vec4 head = Head(uv); //draw head
    vec4 eye = Eye(within(uv, vec4(.03, -.1, .37, .25))); //draw eye
    vec4 mouth = Mouth(within(uv, vec4(-.3, -.4, .3, -.1))); //draw mouth

    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);


    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor =  Smiley(uv);
}
