float func1(float x)
{
    return abs(fract(x) * sin(x));
}
float calVal(vec2 pos)
{
    return func1(iTime*pos.y/pos.x*fract(sin(pos.y/pos.x)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 rtio = vec2(iResolution.x/iResolution.y, 1);
    vec2 p1 = ((fragCoord.xy + vec2(0.25,0.0))/iResolution.xy - vec2(0.5, 0.5))*rtio;
    vec2 p2 = ((fragCoord.xy + vec2(0.75,0.0))/iResolution.xy - vec2(0.5, 0.5))*rtio;
    vec2 p3 = ((fragCoord.xy + vec2(0.25,0.25))/iResolution.xy - vec2(0.5, 0.5))*rtio;
    vec2 p4 = ((fragCoord.xy + vec2(0.75,0.75))/iResolution.xy - vec2(0.5, 0.5))*rtio;
    float val = (calVal(p1) + calVal(p2) + calVal(p3) + calVal(p4))/4.0;
    fragColor = vec4(val-0.2, val+0.2, 0.1, 1.0);
}
