void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 1.0 - fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 direction = normalize(vec2(sin(iTime * 1.7) * cos(0.2 + iTime * 0.4), -6.0));
    vec4 lastColor = 0.333 * (texture(iChannel0, uv + direction * 0.02) + texture(iChannel0, uv + direction * 0.04) + texture(iChannel0, uv + direction * 0.06));
    float emissionAmount = .5 + .333 * (sin(uv.y * 6.1 + iTime * -1.12) + cos(1.0 + uv.x * 5.7 - iTime * 0.7) + sin(1.7 + uv.x * 7.3 + iTime * 2.3));
    vec3 newColor = vec3(0.85, 0.3, 0.9) * emissionAmount * max(0.0, 0.8 - 30.0 * uv.y);
    fragColor = vec4(newColor + .9 * lastColor.rgb * vec3(0.94 + 0.05 * (0.5 * (sin(1.8 + iTime * 2.1) +sin(iTime * 1.3 + 0.6))), 0.9, 1.0),1.0);
}
