void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor = texture( iChannel0, fragCoord/iResolution.xy );    
}

mat2 MR = mat2( .998, -.011, .011, .998 );
mat2 MG = mat2( .996, .002, -.002, .996 );
mat2 MB = mat2( .999, .018, -.018, .999 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
    fragColor.r = texture( iChannel0, (uv-.5)*MR+.5 ).r;
    fragColor.g = texture( iChannel0, (uv-.5)*MG+.5 ).g;
    fragColor.b = texture( iChannel0, (uv-.5)*MB+.5 ).b;

    vec2 m = iMouse.w > 0. ? iMouse.xy/iResolution.xy : vec2( sin( iTime*3. ) * .2, 0. ) + .45;
    if ( length( uv - m ) < .06 )
        fragColor = vec4( sin( iTime )*.5+.5 );
}
