float time = iTime;

float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,
                        _size+vec2(0.001),
                        _st);
    uv *= smoothstep(_size,
                    _size+vec2(0.001),
                    vec2(1.0)-_st);
    return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 s = texture(iChannel0, vec2(20., 20.)) * 2.0 - 1.0;



    uv = uv * 2.0 - 1.0;

    uv.x *= 1.5;

    vec2 c = vec2(0., 0.);

    float d = distance(uv, c);
    float col = s.x;

    for(float i=4.; i<8.; i++){
        float q = sin(time * 1.25) + cos(time * 3.32) + sin(time * 2.2);
        float p = dot(vec2(i, i * q), uv) * cos(time) * (s.x * 0.5);
    	col += step(d, 1.5 - abs(cos(time - uv.x * p) + sin(time + uv.y * p)));
        col += step(d + i, i * 0.0625);

        vec2 boxPos = (uv + vec2(0.5));
        vec2 boxSize = abs(vec2(sin(s.x * i * 0.125) * i, cos(s.y * i * 0.5) * i));;
        col += (box(boxPos, boxSize));

    }

    col /= 8.;

	fragColor = vec4(vec3(col),1.0);
}
