//iq color palette
vec3 pal(float t, vec3 a, vec3 b, vec3 c, vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.-1.;
    uv.x*=iResolution.x/iResolution.y;

    float e = iTime*.125;
    float d = 128.*iMouse.x/iResolution.x+8.5;

    float zoom = 4.+16.*iMouse.y/iResolution.y;
    vec2 g = uv*zoom;
    uv = d*(floor(g)+.5)/zoom;
    g = fract(g)*2.-1.;

    float f = dot(uv,uv)-e;

    vec4 c = vec4(
        pal( f*.5 + e,
            vec3(0.5,0.5,0.5),
            vec3(0.5,0.5,0.5),
            vec3(1.0,1.0,1.0),
            vec3(0.0,0.10,0.20)),1.);

	fragColor = c*(1.-dot(g,g))*.2/abs((fract(f)-.5)*8.);
}
