void mainImage(out vec4 color, in vec2 pixCoords){
    vec2 coords = pixCoords / iResolution.y;
    coords -= vec2(0.5, 0.5);


    vec2 center = vec2(cos(iTime), sin(iTime)) * 0.3;

    vec2 centerToPixel = coords - center;
    float distance = length(centerToPixel);
    float angle = atan(centerToPixel.y, centerToPixel.x);
    float rayon = 0.2 + 0.1 * cos(iTime * 2.0);

    float speed = (mod(distance, 0.4) < 0.2)? 5.0 : -5.0;
    if (mod(distance, 0.2) < 0.05 + 0.02 * cos(angle * 20.0 + iTime * speed )){
        color = vec4(1.0, 0.0, 0.0, 1.0);
    }

    else{
        color = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
