void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float scale = 2.0;
    vec2 left  = vec2( 0.0, 0.0);

    if( uv.y < 0.5) {
       if( uv.x > 0.5) {
            left.x += 0.5;
            scale *= 2.0;
        }
       if( uv.x > 0.75) {
            left.x += 0.25;
            scale *= 2.0;
        }
       if( uv.x > 0.875) {
            left.x += 0.125;
            scale *= 2.0;
        } 

        if( ( 1.0 - uv.y) > uv.x)
	 		fragColor = texture( iChannel0, (uv - left)*scale);
        else
             fragColor = texture( iChannel0, uv);
    }
    else {       
        fragColor = texture( iChannel0, uv);
    }
}
