struct S { float r; };

const int  n = 2;

const S s[n] = S[]( S(1.), S(2.));

#if 1
bool Intersect( S s) { return true; }
#else
bool Intersect( const S s) { return true; }
#endif

void mainImage( out vec4 O, in vec2 U )
{
    for (int i = 0; i < n; ++i)
		if (Intersect(s[i])) O++;
}
