vec2 bubble(vec2 uv, vec2 pos, float radius, float center, float strength){    
    vec2 dir = pos-uv;    
    dir.x *=iResolution.x/iResolution.y;    
    // could substitute smoothstep with other equation for a different curve
    vec2 factor = dir * strength * smoothstep(radius, center, length(dir));
    return factor;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy / iResolution.xy;    
    float aspect = iResolution.x/iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy;
    float radius=0.2;
    float center=0.05;
    float strength = 2.0;	

    float t = clamp(sin(iTime*7.)+1.0/2.0, 0.01, 10.0);

    vec2 b1 = bubble(uv, m, radius*t, center*t, strength);
    b1.x /= aspect;

    //vec2 b2 = bubble(uv, vec2(0.3, 0.7), radius, center, strength);
    //b2.x /= aspect;

    uv += b1;
    //uv += b2;
    vec4 col = texture(iChannel0, uv);

	fragColor = col;

}
