#define RENDER_MODE RENDER_TEXTURE
//#define GRAYSCALE_TEXTURE

const float eta = REFRACTIVE_INDEX_AIR / REFRACTIVE_INDEX_WATER; //ratio of refraction
const float dispersionR = 1. - DISPERSION;
const float dispersionB = 1. + DISPERSION * 1.25;
const float etar = REFRACTIVE_INDEX_AIR / (REFRACTIVE_INDEX_WATER - DISPERSION);
const float etab = REFRACTIVE_INDEX_AIR / (REFRACTIVE_INDEX_WATER + DISPERSION * 1.25);

//coordinate system is left-handed - positive z is away from the viewer
//const float aspectRatio = 9./16.; //inverse of 16:9 aspect ratio
const vec3 viewPos = vec3(.5,.5,-2.); //eye at the center of the image, 2 screen lengths above (avg for fullscreen)
const float specularPower = 32.;
const vec3 lightDir = normalize(SPECULAR_LIGHT_DIR);
const vec3 ncld = normalize(CAUSTICS_LIGHT_DIR);
const vec3 viewDir = vec3(0,0,1); //looking into the screen

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatioCorrection = iResolution.x / iResolution.y;
    vec2 uvCorrected = uv * vec2(aspectRatioCorrection, 1.);

    vec3 rgb, normal;
    vec4 caustic;
    //float dx, dy;
    //vec2 onePixel = 1./iResolution.xy;
   // vec2 onePixelX = vec2(1./iResolution.x, 0.);
    //vec2 onePixelY = vec2(0., 1./iResolution.y);

    #if RENDER_MODE == RENDER_DEPTH
        float depth = texelFetch(iChannel1, ivec2(fragCoord * GRID_RATIO), 0).x * 255.;
        caustic = texelFetch(iChannel2, ivec2(fragCoord * GRID_RATIO), 0);
        //rgb = hsv2rgb(vec3(height,1.,1.));
        if (depth >= 0.)
            fragColor = vec4(depth,0,caustic.r,1); //red is wave trough (shallow water)
        else
            fragColor = vec4(0,-depth,caustic.r,1); //green is wave crest (deeper water)
    #elif RENDER_MODE == RENDER_SPREAD
        vec4 pixel = texelFetch(iChannel0, ivec2(fragCoord * GRID_RATIO), 0);
    	float spread = pixel.w; //no spread is black
    	if (spread < 1.)
            fragColor = vec4(1. - spread, 0., 0., 1); //tighter spread turns red
    	else
            fragColor = vec4(0., 0., spread - 1., 1); //looser spread turns blue
    #elif RENDER_MODE == RENDER_GRADIENT
        normal = texelFetch(iChannel1, ivec2(fragCoord), 0).yzw;
        fragColor = vec4(normal.x, -normal.x, -normal.z, 1);
    #elif RENDER_MODE == RENDER_NORMAL
        normal = texelFetch(iChannel1, ivec2(fragCoord * WATER_GRID_RATIO), 0).yzw;
    	float angle = atan(normal.y/normal.x) / TAU;
    	if (normal.x < 0.) angle += .5;
        rgb = hsv2rgb(vec3(angle, 1., atan(length(normal.xy)) / HALF_PI + .25));
        fragColor = vec4(rgb,1);
    #elif RENDER_MODE == RENDER_CAUSTICS
        //caustic = texelFetch(iChannel2, ivec2(fragCoord * CAUSTICS_GRID_RATIO), 0);
    	caustic = texture(iChannel2, uv * GRID_RATIO);
        /*
        //sharpness filter
        vec4 neighbors = texelFetch(iChannel2, ivec2(fragCoord.x - 1., fragCoord.y), 0)
            + texelFetch(iChannel2, ivec2(fragCoord.x + 1., fragCoord.y), 0)
            + texelFetch(iChannel2, ivec2(fragCoord.x, fragCoord.y - 1.), 0)
            + texelFetch(iChannel2, ivec2(fragCoord.x, fragCoord.y + 1.), 0);
        const float p = 5.;
        caustic = caustic * (1. + 4. * p) - neighbors * p;
		*/
        //vec4 rgbTex = texture(iChannel3, uv);
    	vec4 rgbTex = vec4(1.,1.,1.,1) * .5;
        //vec4 rgbTex = vec4(.66, .85, 1, 1.) * .5;
        fragColor = rgbTex * caustic;
    #elif RENDER_MODE == RENDER_REFRACTION
    		vec3 cDelta = texelFetch(iChannel0, ivec2(fragCoord * GRID_RATIO), 0).yzw;
    	#ifndef ENABLE_CAUSTIC_DISPERSION
            cDelta.xy -= (uv * GRID_RATIO);
    	#endif
    		float angle = atan(cDelta.y / cDelta.x) / PI;
    		if (cDelta.x < 0.) angle += .5;
    		fragColor = vec4(hsv2rgb(vec3(angle,
                                          1.,
                                          atan(length(cDelta.xy) * 30.) / HALF_PI )), 
                             1);
    #else
    	#if PERFORMANCE == 4 
    		vec4 wave = texelFetch(iChannel1, ivec2(fragCoord), 0); //1:1 grid ratio
    	#else
	        vec4 wave = texture(iChannel1, uv * GRID_RATIO);
    	#endif
        float waveDepth = wave.x;


        //calculate local water depth  
        float pWd;// = WATER_DEPTH;
        float ep = .1; 
        float ep2 = 1. - ep;
        float invEp = .5/ep;
        float dd = 0., ddy = 0.;
        if (uv.x < ep) dd = (ep - uv.x) * invEp; //left wall
        else if (uv.x > ep2) dd = (uv.x - ep2) * invEp; //right wall
        if (uv.y < ep) ddy = (ep - uv.y) * invEp; //bottom wall
        else if (uv.y > ep2) ddy = (uv.y - ep2) * invEp; //top wall
        dd = max(dd,ddy);
        pWd = WATER_DEPTH - dd;

        if (waveDepth >= pWd)
        {
            fragColor = texture(iChannel3, uv);
            return;
        }

        //calculate refracted position
        vec3 waterNormal = wave.yzw;
        vec3 waterSurfacePosition = vec3(uv, waveDepth);
        vec3 eyeToWater = waterSurfacePosition - viewPos;
        vec3 rayDir = normalize(eyeToWater);
        float localwd = pWd - waveDepth;
	    vec3 refractDir = refract(rayDir, waterNormal, eta);
    	//extend the ray to where it intersects the floor
        vec2 posShift = refractDir.xy * localwd / (vec2(aspectRatioCorrection, 1.) * refractDir.z);
        vec2 newPos = uv + posShift;


        #ifdef ENABLE_CAUSTICS
        /*
    		if (newPos.x >= 1. && (newPos.x > newPos.y && newPos.x > 1. - newPos.y))
                caustic = CAUSTIC_BASE_COLOR * dot(vec3(SQRT2, 0, SQRT2), ncld);
			else if (newPos.x < 0. && (newPos.x < newPos.y && newPos.x < 1. - newPos.y))
                caustic =  CAUSTIC_BASE_COLOR * dot(vec3(-SQRT2, 0, SQRT2), ncld);
            else if (newPos.y >= 1.)
                caustic = CAUSTIC_BASE_COLOR * dot(vec3(SQRT2, 1, SQRT2), ncld);
            else if (newPos.y < 0.)
                caustic = CAUSTIC_BASE_COLOR * dot(vec3(SQRT2, -1, SQRT2), ncld);
            else
            */
            if (newPos.x < 0. || newPos.x >= 1. ||
                newPos.y < 0. || newPos.y >= 1.)
                caustic = CAUSTIC_BASE_COLOR * CAUSTIC_BRIGHTNESS;
              else
	            caustic = texture(iChannel2, newPos * GRID_RATIO);
        #else
        	#define caustic 1. //compiler should optimize this away
        #endif

        #ifdef ENABLE_DISPERSION
			#ifdef FAST_DISPERSION
    		//calculate dispersion without extra calls to refract(). does not work with grayscale textures
	        vec4 pixelColor = vec4(
                			texture(iChannel3, (uv + posShift * dispersionR)).r, 
                            texture(iChannel3, newPos).g, 
                            texture(iChannel3, (uv + posShift * dispersionB)).b, 1);
            #else
            vec2 rayLen = localwd / (vec2(aspectRatioCorrection, 1.) * refractDir.z); //we could do separate corrections for each ray, but the difference is negligible

            vec2 newPosr = uv + refract(rayDir, waterNormal, etar).xy * rayLen;
            vec2 newPosb = uv + refract(rayDir, waterNormal, etab).xy * rayLen;

            vec4 pixelColor = vec4(texture(iChannel3, newPosr).r, 
                                   texture(iChannel3, newPos).g, 
                                   texture(iChannel3, newPosb).b, 1);
            #endif

        #else
            #ifdef GRAYSCALE_TEXTURE
              vec4 pixelColor = vec4(texture(iChannel3, newPos).r);
            #else
              vec4 pixelColor = texture(iChannel3, newPos);
            #endif
        #endif //dispersion
	    fragColor = pixelColor * caustic;

#ifdef ENABLE_SPECULAR_REFLECTIONS
        //move the light vector like the sun rising and setting
        //we could even do some sunset colors on the specular lighting if we want to get fancy
/*        
float time = fract(iTime * .2)*PI*2.;
if (time > PI) time = PI - (time - PI);
float sinTime = sin(time);
vec3 lightDirection = normalize(vec3(cos(time), sinTime * .5 - .25, sinTime)); 
*/
        float spec = pow(max(0.0, dot(reflect(-lightDir, waterNormal), viewDir)), specularPower);
        fragColor += SPECULAR_COLOR * spec;
        //fragColor = vec4(normalDirection,1);
#endif //specular reflections

    #endif //render_mod

    // Output to screen
    //fragColor = vec4(col,1.0);
}

//packing = x: surface depth, yzw: water normal

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef PAUSE_PHYSICS
    fragColor = texelFetch(iChannel1, ivec2(fragCoord), 0);
    return;
    #endif

    vec2 uv = fragCoord/iResolution.xy;
    if (uv.x > GRID_RATIO || uv.y > GRID_RATIO) {
        fragColor = vec4(0,0,0,0);
        return;
    }
    float onePx = 1. / iResolution.y;

    float oldDepth = texelFetch(iChannel0, ivec2(fragCoord), 0).x;

    //note - using ivec here seems to cause some weird shit to happen (does it?)
    float right = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(1, 0), 0).x;
    float up = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0, 1), 0).x;
    float left =texelFetch(iChannel0, ivec2(fragCoord) - ivec2(1, 0), 0).x;
    float down =  texelFetch(iChannel0,  ivec2(fragCoord) - ivec2(0, 1), 0).x;
    float deltaAvg = (
        right + left +
        up + down)
        * .25 - oldDepth;

    float depth = texelFetch(iChannel1, ivec2(fragCoord), 0).x;

    //precalculate the water normal - points up (-Z) toward the viewport
    //compute the water normal by crossing points on a triangle computed from the derivatives
    //NOTE:Using  dFdx, dFdy is faster than manually calculating derivatives,
    //but doesn't look as good.
    vec3 waterNormal = normalize(cross(vec3(0, onePx, up - down), vec3(onePx, 0, right - left)));

    fragColor = vec4( ((2. * oldDepth - depth) + deltaAvg * 2.) * VISCOSITY
        			, waterNormal);

}

#define rnd(v)   fract( 43758.5453 * sin( v * 78.233 ) ) //stolen from some other shadertoy, forgot which one

float rain_drop_mul = 1. / RAIN_DROP_SIZE;
const vec3 lightDir = normalize(CAUSTICS_LIGHT_DIR); 
const float eta = REFRACTIVE_INDEX_AIR / REFRACTIVE_INDEX_WATER; //ratio of refraction
const float draw_size = float(DRAW_SIZE) * GRID_RATIO;
//const float water_to_caustics_ratio = CAUSTICS_GRID_RATIO / WATER_GRID_RATIO;
const float water_depth = WATER_DEPTH * GRID_RATIO;
const float draw_power = DRAW_POWER * GRID_RATIO;
const vec3 referenceRay = refract(lightDir, vec3(0,0,1.), eta);


//packing = x: water surface depth, yz: photon position (or delta-p), w: photon spread
//or if dispersion is enabled,
//yz: photon position delta xy
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef PAUSE_PHYSICS
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    return;
    #endif

    vec2 uv = fragCoord/iResolution.xy;
    if (uv.x > GRID_RATIO || uv.y > GRID_RATIO) {
        fragColor = vec4(0,0,0,0);
        return;
    }
    float aspectRatioCorrection = iResolution.y / iResolution.x;

    float depth = texelFetch(iChannel0, ivec2(fragCoord), 0).x;

    vec4 oldPix = texelFetch(iChannel1, ivec2(fragCoord), 0);
    float oldDepth = oldPix.x;

	float l = texelFetch(iChannel1, ivec2(fragCoord) - ivec2(1,0), 0).x;
    float r = texelFetch(iChannel1, ivec2(fragCoord) + ivec2(1,0), 0).x;
    float u = texelFetch(iChannel1, ivec2(fragCoord) - ivec2(0,1), 0).x;
    float d = texelFetch(iChannel1, ivec2(fragCoord) + ivec2(0,1), 0).x;

    float deltaAvg = (l+r+u+d) * .25 - oldDepth;

    float result = ((2. * oldDepth - depth) + deltaAvg * 2.) * VISCOSITY;

    #ifdef ENABLE_CAUSTICS
    //calculate refracted ray positions - will be 1 physics-frame old, but saves both speed and space
        //note: maybe could skip calculating the ray if it is beyond the critical angle
    vec3 lightRay = refract(lightDir, oldPix.yzw, eta);
    float lightRayLength = (water_depth - result) / lightRay.z;
    vec2 posShift = vec2(lightRay.x * aspectRatioCorrection, lightRay.y) * lightRayLength;
    vec2 newPos = uv + posShift;
    //compute spread
    float oldArea = (iResolution.x + iResolution.y) / (iResolution.x * iResolution.y);
    float newArea = length(dFdx(newPos)) + length(dFdy(newPos));
    float spread = oldArea / newArea;
    //if (spread < 1.) spread = spread * spread; //further expand unfocused
    #ifdef ENABLE_CAUSTIC_DISPERSION
      //spread = spread * spread; //square the spread if we're using lenSq in BufferC
      vec2 lightIntersect = posShift; //only give the shift
    #else
      vec2 lightIntersect = newPos; //give actual position
    #endif
    #else
    const vec2 lightIntersect = vec2(0, 0);
    const float spread = 1.;
    #endif

    //user input
    if (iMouse.z > 0.) {
        float dist = length(iMouse.xy * GRID_RATIO - fragCoord) / draw_size;
        if (dist <= 1.) {
            result += smoothstep(1.,0.,dist) * draw_power / iResolution.y;
        }
    }

    #ifdef ENABLE_RAIN
    //some quick, very-pseudo random numbers
    vec2 rainPos = iResolution.xy * vec2(rnd(iTime), rnd(iTime + 1.6741));//vec2(fract(iTime * 674.23527), fract((iTime + 1.54361) * 123.32522351));
    float len = 1. + length(fragCoord - rainPos * GRID_RATIO * RAIN_DROP_DELAY);
    if (len < RAIN_DROP_SIZE) {
        result += RAIN_DROP_WEIGHT * smoothstep(1.0, 0., len * rain_drop_mul);
    }
    #endif

    #ifdef ENABLE_WIND
    /* //just a moving circle
    float isp = iTime * WIND_SPEED;
    vec2 wp = ((vec2(cos(isp), sin(isp)) + 
               vec2(cos(isp * WIND_ALPHA), sin(isp * WIND_ALPHA)) * .5)
                     * .33 + .5) * GRID_RATIO;
    float dist = length(wp - uv) / WIND_BETA;
    if (dist <= 1.) {
        result += smoothstep(1.,0.,dist) * WIND_STRENGTH / iResolution.y;
    }
    */

    //add some random disturbance
    //slow waves
    float wind_mul = WIND_FRQ * (2. + sin(iTime));
    float siny = sin(iTime * WIND_SPEED + uv.y * wind_mul);
    result += ( sin(uv.x * wind_mul * 1.78 + siny) ) * WIND_STRENGTH / iResolution.y;
    //float siny2 = sin(iTime * WIND_SPEED * -2. * sin(iTime) + uv.y * WIND_FRQ * 2.17);
    //result += ( sin(uv.x * WIND_FRQ * 3.73 + siny2) )* WIND_STRENGTH / iResolution.y;
    #endif

    fragColor = vec4( result, lightIntersect, spread);
}

//real caustics - make patterns by focusing light
const vec3 lightDir = normalize(CAUSTICS_LIGHT_DIR); //must use overhead vector for fast caustics
const vec3 viewDir = vec3(0,0,1); //looking into the screen
const vec3 water_normal = vec3(0,0,-1);
const float aspectRatio = 16. / 9.;
const float eta = REFRACTIVE_INDEX_AIR / REFRACTIVE_INDEX_WATER; //ratio of refraction
const int num_caustic_steps = int(float(CAUSTIC_SEARCH_RADIUS) / float(CAUSTIC_STEP_SIZE_PX) * GRID_RATIO);

//here we make some derivative constants to balance our defined constants with each other
const float water_depth = WATER_DEPTH * GRID_RATIO;
const int caustic_photon_count = int(pow(float(num_caustic_steps), 2.) * PI); //area of circle
const float inv_caustic_steps = 1. / (1. + float(num_caustic_steps));
const float caustic_step_size_local = float(CAUSTIC_STEP_SIZE_PX) * GRID_RATIO;
const float photon_radius = CAUSTIC_PHOTON_SHARPNESS / float(CAUSTIC_STEP_SIZE_PX);// / (GRID_RATIO);
//get a starting location for our caustic photon search
const vec2 search_offset = refract(lightDir, water_normal, eta).xy * water_depth / refract(lightDir, water_normal, eta).z;
//begin complicated procedure of trying to maintain a proper brightness level
const float photon_brightness = CAUSTIC_BRIGHTNESS
    * pow(photon_radius, 2.)
    * pow(float(CAUSTIC_STEP_SIZE_PX), 1.5)
    / sqrt(float(num_caustic_steps)) 
    * GRID_RATIO * GRID_RATIO
    #ifdef CAUSTIC_CHECKERBOARD
    * 2.
    #endif
    / 1.; //final adjustment term

//for chromatic dispersion
const float dispR = 1. - DISPERSION * 3.; //stronger dispersion factor for caustics
const float dispB = 1. + DISPERSION * 3.; //because it looks better
const float photon_radius_2 = photon_radius * photon_radius * 1.;

#define sigmoid(x) 2./(1.+exp(3.-(3.*x)))

//since we can't project photons onto the ground like we'd like to,
//in the previous buffer we precalculated the location the photon would have landed,
//now we just heave to search all the neighbors to find out which ones casted
//a photon (close to) this position.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#ifndef ENABLE_CAUSTICS
    return;
#endif
    vec2 resolution = iResolution.xy * GRID_RATIO;
    float caustic_step_contribution = photon_brightness / (resolution.x * resolution.y);
    //float aspectRatioCorrection = iResolution.x / iResolution.y;

	vec2 uv = fragCoord / iResolution.xy;
    if (uv.x > GRID_RATIO || uv.y > GRID_RATIO) {
        fragColor = CAUSTIC_BASE_COLOR;
        return;
    }

    ivec2 coord_water = ivec2(fragCoord - search_offset * iResolution.y);
    vec3 uvp = vec3(uv, water_depth);

    #ifdef CAUSTIC_CHECKERBOARD
    	int fp = (iFrame % 2);
    	//int pixelOffset = fp * CAUSTIC_STEP_SIZE_PX;
        //coord_water.y -= pixelOffset; //every other
    	const int I_STEP = 2;
    #else
    	const int fp = 0;
    	const int I_STEP = 1;
    #endif

    float causticP = 0.; //colorless brightness
    vec3 causticC = vec3(0,0,0); //colored brightness
    int cStep = CAUSTIC_STEP_SIZE_PX * I_STEP;
    int py = coord_water.y - (num_caustic_steps + 1) * CAUSTIC_STEP_SIZE_PX; //starting y-coord
    for (int i = -num_caustic_steps; i <= num_caustic_steps; i += 1) {
        py += CAUSTIC_STEP_SIZE_PX;

        //some early bailing... seems to be the same speed with or without but may help on some cards
        if (py < 0) continue; //skip this row
        if (py >= int(resolution.y) - 1) break; //nothing more to see here

        #ifdef CAUSTIC_CHECKERBOARD
        	#define DO_OFFSET + ((py + fp) % 2);
        #else
        	#define DO_OFFSET
        #endif
        float mul = cos(float(i) * (inv_caustic_steps * HALF_PI));
        int xWidth = int(float(CAUSTIC_STEP_SIZE_PX * num_caustic_steps) * mul);
        int px0 = max(coord_water.x - xWidth, 0) DO_OFFSET;
        int px1 = min(coord_water.x + xWidth, int(iResolution.x * GRID_RATIO));

        for (int px = px0; px <= px1; px += cStep) {
            vec4 wave = texelFetch(iChannel0, ivec2(px, py), 0);
          #ifdef ENABLE_CAUSTIC_DISPERSION 
            //[1]
            //fast dispersion approximation
            vec2 wCoord = (vec2(px, py) / iResolution.xy) - uv; //water test pos, relative to floor pos
            float dist = length(wCoord + wave.yz);
            vec3 distLItoUV = vec3(length((wCoord + wave.yz * dispR) ),
                                   length((wCoord + wave.yz)),
                                   length((wCoord + wave.yz * dispB)) );
            float focus, br;
            br = wave.w ;
            focus = wave.w * photon_radius;
            vec3 len = (1. - distLItoUV * focus) * caustic_step_contribution * br * br;
            causticC += max(len, 0.);
          #else
            float focus = wave.w * photon_radius;
            float br = wave.w;
            //if (abs(wave.w - (1. + sin(iTime))) > .1) br = 0.;
            float distLItoUV = length(wave.yz - uv);            
            causticP += max((1. - distLItoUV * focus) * caustic_step_contribution * br * br, 0.);
          #endif
        } //next px
    } //next py

    fragColor = 
    #ifdef CAUSTIC_CHECKERBOARD
	    mix(texelFetch(iChannel2, ivec2(fragCoord), 0), 
    #endif
    #ifdef ENABLE_CAUSTIC_DISPERSION
    	CAUSTIC_BASE_COLOR + vec4(causticC, 1.)
    #else
    	fragColor = CAUSTIC_BASE_COLOR + vec4(causticP, causticP, causticP, 1.0)
    #endif //dispersion
    #ifdef CAUSTIC_CHECKERBOARD
    	, .75)
    #endif //caustic frame blending
        ;
}

/* [1]
            //very slow dispersion calculations
            vec2 waterUv = vec2(px, py) / iResolution.xy;
            vec2 aspectRatioCorrection = vec2(iResolution.y / iResolution.x, 1.);
            vec3 normal = texelFetch(iChannel1, ivec2(px, py), 0).yzw;
            float lwd = water_depth - wave.x;
            vec3 lightRayR = refract(lightDir, normal, etar);
            vec3 lightRayB = refract(lightDir, normal, etab);
            vec2 lpr = (waterUv + lightRayR.xy * aspectRatioCorrection * (lwd / lightRayR.z)) * water_to_caustics_ratio;
            vec2 lpb = (waterUv + lightRayB.xy * aspectRatioCorrection * (lwd / lightRayB.z)) * water_to_caustics_ratio;           
            vec3 len =1. - photon_radius * vec3(length(lpr - uv), length(lightIntersect - uv), length(lpb - uv));
*/
