//saturate from hlsl
float saturate(float a)
{
return clamp(a,0.0,1.0);
}


void mainImage( out vec4 ts, in vec2 fragCoord )
{
vec2 uv = (fragCoord/iResolution.xy)*vec2(1.777,1.0)- vec2(0.3885,0.0);
float TSF = 0.0;
float Levels =7.0;
float S = 2.0;
int T = int(fract(iTime*0.2)*8.0)+1;

for(int i=0;i < T; i++)
{
if(i==0)
{
TSF = round(min(min(fract(uv).x,1.0-fract(uv).x),min(fract(uv).y,1.0-fract(uv).y))*2.0);
}
else
{
TSF += round(min(min(fract(uv*S).x,1.0-fract(uv*S).x),min(fract(uv*S).y,1.0-fract(uv*S).y))*2.0);
S *=2.0;
}
} 

TSF = saturate(TSF);

    // Output to screen
    ts = vec4(TSF,TSF,TSF,1.0);
}
