// MISC Variables
const float WINDOW_OFFSET = 0.3;
// Set to 0.0 to disable the Body
const float BODY_SWITCH = 1.0;
// Set to 0.0 to disable the Chimney
const float CHIMNEY_SWITCH = 1.0;
// Set to 0.0 to disable the Window(s)
const float WINDOW_SWITCH = 1.0;

// Body Variables
vec2 bodyLowerLeft = vec2(0.6,0.0);
float bodyWidth = 0.6;

// Chimney Variables
vec2 chimneyLowerLeft = vec2(0.8, 0.6);
float chimneyWidth = 0.2;

// Window Variables
vec2 windowLowerLeft = vec2(0.65, 0.3);
float windowWidth = 0.2;

// Color Variables
vec3 bodyCol = vec3(1.0,0.0,0.0);
vec3 chimneyCol = vec3(0.0,1.0,0.0);
vec3 windowCol = vec3(0.0,0.0,1.0);

float createSquare(vec2 cornerPos, vec2 screenPos, float width, float visibility)
{	// Binds X-Coordinate between LowerLeft x-pos & LowerLeft+Width x-pos
    float interpX = step(cornerPos.x,screenPos.x) - step(cornerPos.x+width,screenPos.x);
    // Binds Y-Coordinate between LowerLeft y-pos & LowerLeft+Width y-pos
    float interpY = step(cornerPos.y,screenPos.y) - step(cornerPos.y+width,screenPos.y);
    // Returns the constructed square in the passed color
    return (interpX*interpY)*visibility;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 bgCol = vec3(1.0,1.0-uv.y,0.0);
    // Creates an x-focused aspect ratio correction
    float aspectRatio = iResolution.x / iResolution.y;
    // Corrects the normalized view by the aspect ratio
    uv.x *= aspectRatio;
    // Initializes Layers for Output
    vec4 BGLayer = vec4(bgCol, 1.0);
    vec4 LayerOne = vec4(1.0,0.0,0.0,0.0);
    vec4 LayerTwo = vec4(0.0,1.0,0.0,0.0);
    vec4 LayerThree = vec4(0.0,1.0,0.0,0.0);

    // Body position, color, size
    float body = createSquare(bodyLowerLeft, uv, bodyWidth, BODY_SWITCH);
    LayerOne = vec4(bodyCol, body);
    // Chimney position, color, size
    float chimney = createSquare(chimneyLowerLeft, uv, chimneyWidth, CHIMNEY_SWITCH);
    LayerTwo = vec4(chimneyCol, chimney);
    // Window(s) position, color, size
    float winOneSquare = createSquare(windowLowerLeft, uv, windowWidth, WINDOW_SWITCH);
    float winTwoSquare = createSquare(vec2(windowLowerLeft.x+WINDOW_OFFSET,windowLowerLeft.y), uv, windowWidth, WINDOW_SWITCH);
    // Combines windows into a single variable
    float window = winOneSquare+winTwoSquare;
    LayerThree = vec4(windowCol, window);

    // Mixes the Background, Body, Chimney, and Windows into distinct layers
    vec4 outputCol = mix(BGLayer, LayerOne,LayerOne.a);
    outputCol = mix(outputCol, LayerTwo,LayerTwo.a);
    outputCol = mix(outputCol, LayerThree, LayerThree.a);

    fragColor = outputCol;
}
