#define T .04 // Thickness
#define S 12. // Sharpness
#define B 1.

float line(float uvy, float center)
{
    uvy -= center;

    return pow(T / abs(uvy), S);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    //float centerPos = sin(iTime) * 0.5 + 0.5;
    //float centerPos = abs(fract(iTime*0.4));
    float centerPos = fract(iTime*0.1) * (1.+B) - (B*0.5);

	fragColor = vec4(vec3(line(p.y, centerPos)), 1);
}
