#define LINEWIDTH (2.0 / iResolution.x)
#define NOISEGAIN (1.0 / 256.0)
#define RAND_MULT_PHASE 10.0


//#define USE_MOUSE
//#define USE_TEXTURE_NOISE

#ifdef USE_TEXTURE_NOISE
float rand(vec2 n) { 
	return texture(iChannel0, n).x;
}
#else //USE_TEXTURE_NOISE
float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1))) * 43758.53);
}
#endif //USE_TEXTURE_NOISE

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = ( fragCoord / iResolution.xy ) * 2.0 - 1.0;
    vec2 auv = uv * vec2(1.0, iResolution.y /  iResolution.x);
	vec3 basecol0 = vec3(1.0,0.1,1.0 * sin(iTime)) * 0.5;
	vec3 basecol1 = vec3(0.1,0.0,1.0) * 0.5;

	float rk = 0.0;

    float xoffset = uv.x;
#ifdef USE_MOUSE
    xoffset +=  -(iMouse.x / iResolution.x) * 2.0 + 1.0;
#endif //USE_MOUSE
	if(xoffset > 0.0) {
        rk = rand(auv + iTime) * 2.0 - 1.0;
	}

	if(abs(xoffset) < LINEWIDTH) {
		fragColor.xyz = vec3(1.0);
		fragColor.a = 1.0;
		return;
	}
    float ky = 1.0 - (uv.y + rk * NOISEGAIN);
	vec3 col = pow(mix(basecol0, basecol1, ky), vec3(1.0 / 5.0));
    //vec3 col = vec3(rk);
	fragColor = vec4(col, 1.0);
}
