vec4 oldColor;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 texSample 	= texture( iChannel0, uv ).rgb;
    vec3 texSample2 = texture( iChannel1, uv ).rgb;

    if(texSample.g < 0.4 || (texSample.b > 0.2 && texSample.r > 0.2)){
        if(texSample2.g < 0.4 || (texSample2.b > 0.2 && texSample2.r > 0.2)){
            vec3 mixColor = mix(texSample, texSample2, .5);
        	fragColor = vec4(mixColor, 1.0);
        }
        else{
        	fragColor = vec4(texSample, 1.0);  
        }
    }
    else if (texSample2.g < 0.4 || (texSample2.b > 0.2 && texSample2.r > 0.2)){
       fragColor = vec4(texSample2, 1.0); 
    }
    else{
        //fragColor = vec4(0.2,0.8,0.2,1.0);
        fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    }


}
