vec4 get_radial_blur(in vec2 uv) 
{
    vec4 result = vec4(0.0);
    vec4 color = texture(iChannel0, vec2(uv.x, -uv.y));
    const int num_samples = 5;
    vec2 center = iMouse.xy / iResolution.xy;
    float scale = .015;
    float dist = length(uv - center);
    float strength = 10.1;
    for(int i =- num_samples / 2; i <= num_samples / 2; i++)
    {
        float stepsize = scale * (float(i) / float(num_samples));
        vec2 coord = uv + (uv - center) / dist * stepsize; 
        result += texture(iChannel0, vec2(coord.x, -coord.y));
    }
    return mix(color, result / float(num_samples), clamp(dist * strength, 0.0, 1.0));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = get_radial_blur(uv);
	fragColor = color;
}
