float h1(vec2 p)
{
 	return fract(cos(p.x*89.42-p.y*75.7)*343.42);
}
vec2 h2(vec2 p)
{
 	return fract(cos(p*mat2(89.4,-75.7,-81.9,79.6))*343.42); 
}
float voronoi(vec2 n,float s)
{
    float i = 0.0;
    float dis = 2.0;
    for(int x = -1;x<=1;x++)
    for(int y = -1;y<=1;y++)
    {
        vec2 p = floor(n/s)+vec2(x,y);
        float d = length(h2(p)+vec2(x,y)-fract(n/s));
        if (dis>d)
        {
            dis = d;
            i = h1(p);
        }
    }
    return i;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 coord = fragCoord.xy+iTime*32.0;
	fragColor = vec4(vec3(voronoi(coord,32.0)),1.0);
}
