#define rndB(U) texture(iChannel0, U)     // bilinear interpolation

vec4 rndC(vec2 u) {                       // bicubic interpolation ( 1 single fetch: thanks iq :-) )
    vec2 R = iChannelResolution[0].xy,
         U = u*R + .5,
         F = fract(U);
    U = floor(U) + F*F*(3.-2.*F); 
 // U = floor(U) + F*F*F*(F*(F*6.-15.)+10.);   // use if you want smooth gradients
    return texture( iChannel0, (U-.5) / R );
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, V;
    U = ( U+U - R ) / R.y;
    V = .1*( U - .1*iTime );

	O =   U.y > 2./R.y ? rndC(V)          // bicubic (null-tangent)
        : U.y > 0.     ? vec4(14) 
        :                rndB(V);         // bilinear
    if (U.x>0.) O = sin(10.*O);
  //O = sqrt(O);                          // gamma correction
}


