vec2 stepj(in vec2 cx, in vec2 p) {
    return vec2(cx.x * cx.x - cx.y * cx.y + p.x, cx.x * cx.y + cx.y * cx.x + p.y);
}

float iter(vec2 cx) 
{
    for (int i = 0; i < 200; i++) {
        float s = iTime;
        float r = 0.48;
    	float t = s;

    	vec2 p = vec2(r * cos(t) - 0.25, r * sin(t));

        cx = stepj(cx, p);
    }

    return length(cx);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 cx = fragCoord.xy / iResolution.xy * 3.0 - vec2(1.5, 1.5);

    float d = iter(cx) / iter (stepj(cx, vec2(0.0, 0.0)));

    fragColor = vec4(d * 0.45, d * 0.05, d * 0.95, 1.0);

}
