void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
   	vec4 col = texture(iChannel0, uv);

    float mul = 2.;

    col += texture(iChannel0, uv - vec2(.001, 0.) * mul);
    col += texture(iChannel0, uv - vec2(.002, 0.) * mul);
    col += texture(iChannel0, uv - vec2(.003, 0.) * mul);
    col += texture(iChannel0, uv - vec2(.004, 0.) * mul);
    col += texture(iChannel0, uv - vec2(.005, 0.) * mul);
    col += texture(iChannel0, uv + vec2(.001, 0.) * mul);
    col += texture(iChannel0, uv + vec2(.002, 0.) * mul);
    col += texture(iChannel0, uv + vec2(.003, 0.) * mul);
    col += texture(iChannel0, uv + vec2(.004, 0.) * mul);
    col += texture(iChannel0, uv + vec2(.005, 0.) * mul);

    col += texture(iChannel0, uv - vec2(0., .001) * mul);
    col += texture(iChannel0, uv - vec2(0., .002) * mul);
    col += texture(iChannel0, uv - vec2(0., .003) * mul);
    col += texture(iChannel0, uv - vec2(0., .004) * mul);
    col += texture(iChannel0, uv - vec2(0., .004) * mul);
    col += texture(iChannel0, uv + vec2(0., .001) * mul);
    col += texture(iChannel0, uv + vec2(0., .002) * mul);
    col += texture(iChannel0, uv + vec2(0., .003) * mul);
    col += texture(iChannel0, uv + vec2(0., .004) * mul);
    col += texture(iChannel0, uv + vec2(0., .005) * mul);

	fragColor = col / 21.;
}
