// Assumes input is a 3D side by side. (which it is not)



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 xy = fragCoord.xy / iResolution.xy;
    vec2 xyL = xy;
    xyL.x = xyL.x * 0.5;
    vec2 xyR = xy;
    xyR.x = (xyR.x * 0.5) + 0.5;



    vec4 texColorL = texture(iChannel0,xyL);
    vec4 texColorR = texture(iChannel0,xyR);
    fragColor.x = texColorL.x;
    fragColor.yz = texColorR.yz;
}
