void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 offset = vec2(cos(iTime * 10.0), sin(iTime * 10.0)) * 0.01;

	vec4 sampleA = texture(iChannel0, uv + offset);
	vec4 sampleB = texture(iChannel0, uv);

	float value = length(sampleA.rgb) / 3.0;

	value = value > 0.25?1.0:value;

	vec3 color = sampleB.rgb;

    fragColor = vec4(color, 1.0) * value;
}
