void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float blocksize = 5.0 + iResolution.y / 25.0;
    vec2 within_block = mod(fragCoord.xy, blocksize) - vec2(0.5 * blocksize);
    vec2 block = fragCoord.xy - within_block;
	vec2 uv = block.xy / iResolution.xy;
	vec2 flow  = textureLod(iChannel0, uv, 2.0).rg - vec2(0.5);
    float lineness = abs(dot(normalize(flow.yx * vec2(-1.0, 1.0)), within_block)); //  / dot(flow, flow);
    float alongness = (dot(flow, within_block)/blocksize);
    float dark = smoothstep(0.2 * blocksize, 0.0, lineness) *
        step(alongness, dot(flow, flow)) * step(0.0, alongness);
    float ballness = smoothstep(3.0, 1.0, dot(within_block, within_block));
    if (dot(flow, flow) < 1.0e-6) {
        fragColor = vec4(vec3(ballness), 1.0);
    } else {
	    fragColor = vec4(vec3(dark + ballness), 1.0);
	}
}

const float lod = 3.0;
const float lodscale = 8.0; // 2 ^ lod


float intensity(vec2 loc, float time) {
	float i0 = dot(textureLod(iChannel0, loc, lod).rgb, vec3(1.0));
    float i1 = dot(textureLod(iChannel1, loc, lod).rgb, vec3(1.0));
    return mix(i0, i1, time);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // lucas-kanade optical flow 
    // https://en.wikipedia.org/wiki/Lucas%E2%80%93Kanade_method
    mat2 AtA = mat2(0.0, 0.0, 0.0, 0.0);
    vec2 Atb = vec2(0.0, 0.0);
    vec2 p = (fragCoord.xy - vec2(5.0)) /iResolution.xy;
    float xstart = p.x;
    vec2 px_step = lodscale / iResolution.xy;
    for (int i = 0; i < 11; ++i) {
        p.x = xstart;
        for (int j = 0; j < 11; ++j) {
            float I = intensity(p, 0.0);
            float It = I - intensity(p, 1.0);
            float Ix = intensity(p + vec2(1.0, 0.0) * px_step, 0.0) - I;
            float Iy = intensity(p + vec2(0.0, 1.0) * px_step, 0.0) - I;

            AtA += mat2(Ix * Ix, Ix * Iy, Ix * Iy, Iy * Iy);
            Atb -= vec2(It * Ix, It * Iy);
            p.x += px_step.x;
        }
        p.y += px_step.y;
    }
    mat2 AtAinv = mat2(AtA[0][0], -AtA[0][1], -AtA[1][0], AtA[1][1]) /
        (AtA[0][0] * AtA[1][1] - AtA[1][0] * AtA[0][1]);

    vec2 flow = AtAinv * Atb;

    fragColor = vec4(0.5 + 0.1 * flow,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy);
}
