void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 p = uv;
	fragColor = texture(iChannel0, uv - mod(uv, 0.02));
    uv -= mod(uv, 0.04 * (1. - uv.y) * 3.);
    uv.x -= sin(uv.y * 10. - iTime * 0.4) * 0.2;
    float r = smoothstep(0., 0.01, length(uv) - 0.4);
    p.x += sin(p.y * 300. + iTime) * 3.;
    fragColor = vec4(1. - r) * vec4(p.y + 0.632, p.y * 5. - 0.9, p.y * 1.5, 0.) + r * vec4(1., 0.25, (1. - abs(p.x)) * 0.75, 1.);
    fragColor += exp(p.x) * 0.0025;
}
