// By Michael Zheng, Dianna Cornett, Joshua Mahler
// Intro to Graphics Programming at Champlain College
// 10/6/2017
mat3 darknight = mat3(
 0, 2, 0, // first column (not row!)
 2, -8, 2, // second column
 0, 2, 0 // third column 
);

vec3 aSample(const int x, const int y, in vec2 fragCoord)
{
 vec2 uv = fragCoord.xy / iResolution.xy * iChannelResolution[0].xy;
 uv = (uv + vec2(x, y)) / iChannelResolution[0].xy ;
 return texture(iChannel0, uv).xyz;
}

vec3 aFilter(in vec2 fragCoord, mat3 kernel)
{
 vec3 sum = vec3 (0.0,0.0,0.0);;

 for (int first = 0; first < 3; first++)
 {
     for(int second = 0; second < 3; second++)
     {
      sum += aSample(first-1,second-1, fragCoord) * kernel[first][second];
     }
 }

 return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord.xy/iResolution.xy;

vec2 fc = fragCoord.xy;
vec3 cf = aFilter(fc, darknight);

vec3 newColor = vec3(1.0-cf.r,1.0-cf.g,1.0-cf.b);
fragColor = vec4(newColor,1.0);
}
