vec2 laplacian2D(vec2 fragCoord, vec2 dir) 
{
    float st = 1.;
    return 
        cell(fragCoord, vec2(0., -st), dir, .2) +
        cell(fragCoord, vec2(0., st), dir, .2) +
        cell(fragCoord, vec2(st, 0.), dir, .2) +
        cell(fragCoord, vec2(-st, 0.), dir, .2) +
        cell(fragCoord, vec2(-st, -st), dir, .05) +
        cell(fragCoord, vec2(-st, st), dir, .05) +
        cell(fragCoord, vec2(st, -st), dir, .05) +
        cell(fragCoord, vec2(st, st), dir, .05) -
        cell(fragCoord, vec2(0., 0.), dir, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 mo = iMouse.xy / iResolution.xy;

   	fragColor = vec4(0,0,0,0);

    if (iFrame < 120)
    {
    	fragColor = texture(iChannel1, uv).rgrg;
    }
    else if (iMouse.z > 0.0)
	{
		fragColor = texture(iChannel0, uv);
		if (length(uv-mo) < 0.1)
			fragColor = mix(vec4(0,1,0,1), vec4(1,0,0,1), length(uv-mo)); 
	}
    else
    {
        float a = iTime * 3.;

		vec2 dir = vec2(cos(a),sin(a));

        vec2 diffusionCoef = vec2(1,.3);
        float feedCoef = 0.05;
        float killCoef = 0.062;

        vec2 ab = cell(fragCoord, vec2(0,0), vec2(0,0), 1.);
        vec2 lp = laplacian2D(fragCoord, dir);

        float reaction = ab.x * ab.y * ab.y;
        vec2 diffusion = diffusionCoef * lp;
        float feed = feedCoef * (1. - ab.x);
        float kill = (feedCoef + killCoef) * ab.y;

        ab += diffusion + vec2(feed - reaction, reaction - kill);

    	fragColor = vec4(clamp(ab,0.,1e4),0.0,1.0);
    }
}

