float mxt(vec2 col)
{
    return length(sqrt(col));
}
float trace(float shape)
{
    float sabs = abs(shape);
    sabs *= distance(shape/iResolution.x, shape*iResolution.x);
    float q = sqrt(sabs) / length(shape);
    return clamp(q, 0.0, 1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 x = normalize(vec3(uv, fragColor.w)) - uv.y + uv.x;
    float circle = length(vec2(uv.x*2.0-1.0*(iResolution.x/iResolution.y),uv.y*2.0-1.0));
    circle -= sin(uv.y)+fract(iTime+tan(circle/2.0));

    float re = sqrt(circle);
    vec3 qw = x + trace(circle);
    vec4 ar = vec4(mxt(qw.xy - uv));
	fragColor = vec4(sqrt(x)-re, 1.0)-vec4(qw,1.0)+ar;
}
