void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 g = fragCoord.xy;
	vec2 s = iResolution.xy;
	vec2 uv = (g+g-s)/s.y*3.;

	uv = vec2(atan(uv.x/uv.y)/atan(-1.), length(uv));

	uv.y += uv.x;
	uv.x += uv.y;
	uv.y -= iDate.w * 0.1;
	uv.x += iDate.w;

	uv = abs(fract(uv)-0.5);

	float r = length(uv)*2.;
	uv += vec2(cos(r),sin(r))*r;

	fragColor = 1.2 - vec4(max(uv.x, uv.y)); //original
    //fragColor = 1.2 - vec4(uv.x, uv.y, max(uv.x, uv.y), 1.); // by iapafoto
}
