// Gigat(Ron)an France for Shadertoy ; quick pattern test !
//
#define PI 3.14159265358979323846

#define Qfact 64.0


vec2 rotate2D(vec2 v, float _angle){
    v -= 0.5;
    v =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * v;
    v += 0.5;
    return  v;
}
// 2d rotation from venus ! 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t=2.+2.0*abs(sin(iTime*60.)-1.);
    float t2=iTime;

   /*------           Q1vec(x,x,x,x,y,y,y,y,z,z,z,z,w,w,w,w.............................

    */

    vec2 muv=uv;     // motif uv 
    uv=-muv;

    uv *= vec2(5.0,5.0)*t/2.;

    uv=rotate2D(uv,PI*t2*60.);

    float fct = mod((uv.x - uv.y), 0.08) / Qfact + mod((uv.x + uv.y), 0.08) / Qfact;

    fragColor =vec4(texture(iChannel0,muv+vec2(fct*16. ))) ;

}
