float modulo( float value ) {
    return value / 2.0 - floor(value / 2.0) > 0.25 ? 0.0 : 1.0;
}

float wobbleBox(
    in vec2 fragCoord,
    in float amplitude,
    in float ampWobble,
    in float frequency,
    in float wavelength,
    in float thickness
)
{

    float yOffset = ((1.0 - (sin(iTime) * ampWobble)) * amplitude) * iResolution.y * sin(iTime * frequency + (wavelength * fragCoord.x / iResolution.x));

    float value = true
        && fragCoord.x > iResolution.x * 0.0
        && fragCoord.x < iResolution.x * 1.0
        && fragCoord.y > iResolution.y * (0.5 - thickness) + yOffset
        && fragCoord.y < iResolution.y * (0.5 + thickness)  + yOffset
        ? 1.0 : 0.0;

//    value = modulo(fragCoord.y) == 1.0 ? value : 0.0;

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor = vec4(
        wobbleBox(fragCoord, 0.1, 1.0, 10.0, 15.0, 0.05),
        wobbleBox(fragCoord, 0.1, 2.0, 15.0, 16.0, 0.05),
        wobbleBox(fragCoord, 0.1, 3.0, 20.0, 17.0, 0.05),
		1
    );
}
