#define BG_COLOR vec3(1.0, 1.0, 1.0)
#define FG_COLOR vec3(0.0, 0.0, 0.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame == 0) {
        // initialize
        fragColor = vec4(BG_COLOR, 1.0);
    } else {
        // read the old color
        vec3 color = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;

        // fade away a fraction
        float fraction = 0.1;
        vec3 k = 1.0/vec3(1.4);
        color = pow(pow(color, k)*(1.0 - fraction) + pow(BG_COLOR, k)*fraction, 1.0/k);

        // normalized device coordinates, aspect ratio corrected
        vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.yy;
        p *= 2.0; // zoom out

        // define disk location
        float r = 0.25*0.125;
        float f = 0.5;
        vec2 c = vec2(2.0*cos(f*iTime), sin(f*iTime*5.0));

        // distance to disk
        float d = length(p - c) - r;
        float s = smoothstep(0.0, 0.01, d);

        // blend the current disk
        color = FG_COLOR*(1.0 - s) + color*s;

        //
    	fragColor = vec4(color, 1.0);    
    }
}
